import * as VIAM from '@viamrobotics/sdk';

export const getRobotClient = async () => {
  const host = 'bop2025-main.1qcq9pmop4.viam.cloud';
  const credentials = {
    type: 'api-key',
    payload: 'hxtk88rojiq2pzc7nasiiymnbfxr1dyc',
    authEntity: '2597529f-b98b-4e62-8c94-7f99501b0a76',
  };

  console.log('Connecting to robot at:', host);

  try {
    const client = await VIAM.createRobotClient({
      host,
      credentials,
      signalingAddress: 'https://app.viam.com:443',
    });

    console.log('Connected successfully, getting resources...');
    const resources = await client.resourceNames();
    console.log('Available resources:', resources);

    return client;
  } catch (error) {
    console.error('Detailed connection error:', error);
    throw error;
  }
};

export const getCameraClient = (robotClient, cameraName) => {
  console.log(`Creating camera client for: ${cameraName}`);
  return new VIAM.CameraClient(robotClient, cameraName);
};

export const getSensorClient = (robotClient, sensorName) => {
  console.log(`Creating sensor client for: ${sensorName}`);
  return new VIAM.SensorClient(robotClient, sensorName);
};

export const getBoardClient = (robotClient, boardName) => {
  console.log(`Creating board client for: ${boardName}`);
  return new VIAM.BoardClient(robotClient, boardName);
};
