import React, { useEffect } from 'react';
import { useRobotStore } from './state.js';

function App() {
  const { connect, disconnect, connected, connecting, error } = useRobotStore();

  useEffect(() => {
    // Don't auto-connect, let user manually connect
  }, []);

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '1rem' }}>
      <header style={{ marginBottom: '1.5rem' }}>
        <h1 style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>Viam Robot Dashboard</h1>
        <div style={{ marginTop: '0.5rem' }}>
          {connected ? (
            <div>
              <p style={{ color: 'green', fontWeight: 'bold' }}>Successfully connected to robot!</p>
              <button 
                onClick={disconnect}
                style={{ 
                  padding: '0.5rem 1rem', 
                  backgroundColor: '#ef4444', 
                  color: 'white', 
                  borderRadius: '0.375rem',
                  border: 'none',
                  cursor: 'pointer'
                }}
              >
                Disconnect
              </button>
            </div>
          ) : (
            <div>
              <button 
                onClick={connect}
                style={{ 
                  padding: '0.5rem 1rem', 
                  backgroundColor: '#3b82f6', 
                  color: 'white', 
                  borderRadius: '0.375rem',
                  border: 'none',
                  cursor: 'pointer',
                  opacity: connecting ? 0.7 : 1
                }}
                disabled={connecting}
              >
                {connecting ? 'Connecting...' : 'Connect'}
              </button>
              {error && <p style={{ color: '#ef4444', marginTop: '0.5rem' }}>{error}</p>}
            </div>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
