import React, { useEffect } from 'react';
import { useRobotStore } from './state';
import CameraView from './components/CameraView';
import SensorDisplay from './components/SensorDisplay';

function App() {
  const { connect, disconnect, connected, connecting, error } = useRobotStore();

  useEffect(() => {
    // Auto-connect when the app loads
    connect();

    return () => {
      disconnect();
    };
  }, [connect, disconnect]);

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '1rem' }}>
      <header style={{ marginBottom: '1.5rem' }}>
        <h1 style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>Viam Robot Dashboard</h1>
        <div style={{ marginTop: '0.5rem' }}>
          {connected ? (
            <button 
              onClick={disconnect}
              style={{ 
                padding: '0.5rem 1rem', 
                backgroundColor: '#ef4444', 
                color: 'white', 
                borderRadius: '0.375rem',
                border: 'none',
                cursor: 'pointer'
              }}
            >
              Disconnect
            </button>
          ) : (
            <button 
              onClick={connect}
              style={{ 
                padding: '0.5rem 1rem', 
                backgroundColor: '#3b82f6', 
                color: 'white', 
                borderRadius: '0.375rem',
                border: 'none',
                cursor: 'pointer',
                opacity: connecting ? 0.7 : 1
              }}
              disabled={connecting}
            >
              {connecting ? 'Connecting...' : 'Connect'}
            </button>
          )}
          {error && <p style={{ color: '#ef4444', marginTop: '0.5rem' }}>{error}</p>}
        </div>
      </header>

      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(500px, 1fr))', 
        gap: '1rem' 
      }}>
        <CameraView cameraName="camera-1" />
        <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
          <SensorDisplay sensorName="temperature" />
          <SensorDisplay sensorName="sensor-1" />
          <SensorDisplay sensorName="gateway-waveshare" />
        </div>
      </div>
    </div>
  );
}

export default App;
