import { create } from 'zustand';
import * as VIAM from '@viamrobotics/sdk';
import { getRobotClient, getSensorClient } from './client.js';

export const useRobotStore = create((set, get) => ({
  robotClient: null,
  connected: false,
  connecting: false,
  error: null,
  connect: async () => {
    try {
      set({ connecting: true, error: null });
      const client = await getRobotClient();
      set({ robotClient: client, connected: true, connecting: false });
    } catch (error) {
      set({
        error: `Failed to connect: ${error instanceof Error ? error.message : String(error)}`,
        connecting: false,
      });
    }
  },
  disconnect: async () => {
    const { robotClient } = get();
    if (robotClient) {
      // Simply update the state
      set({ robotClient: null, connected: false });
    }
  },
  getSensorReadings: async (sensorName) => {
    const { robotClient } = get();
    if (!robotClient) throw new Error("Not connected to robot");

    const sensorClient = getSensorClient(robotClient, sensorName);
    return await sensorClient.getReadings();
  },
}));
