import React, { useState } from 'react';
import * as VIAM from '@viamrobotics/sdk';

function App() {
  const [status, setStatus] = useState('disconnected');
  const [error, setError] = useState(null);
  const [resources, setResources] = useState([]);

  const connect = async () => {
    try {
      setStatus('connecting');
      setError(null);

      console.log('Starting connection process...');
      const host = 'bop2025-main.1qcq9pmop4.viam.cloud';

      // This is the exact same code that worked in your test
      const machine = await VIAM.createRobotClient({
        host,
        credentials: {
          type: 'api-key',
          payload: 'hxtk88rojiq2pzc7nasiiymnbfxr1dyc',
          authEntity: '2597529f-b98b-4e62-8c94-7f99501b0a76',
        },
        signalingAddress: 'https://app.viam.com:443',
      });

      console.log('Connection successful!');

      // Test getting resources
      try {
        console.log('Getting resources...');
        const resourceList = await machine.resourceNames();
        console.log('Resources:', resourceList);
        setResources(resourceList);
      } catch (resourceError) {
        console.error('Error getting resources:', resourceError);
      }

      setStatus('connected');
    } catch (connectionError) {
      console.error('Connection error:', connectionError);
      setError(`Failed to connect: ${connectionError.message || 'Unknown error'}`);
      setStatus('disconnected');
    }
  };

  const disconnect = () => {
    setResources([]);
    setStatus('disconnected');
  };

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '1rem' }}>
      <header style={{ marginBottom: '1.5rem' }}>
        <h1 style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>Viam Robot Direct Connection Test</h1>
        <div style={{ marginTop: '0.5rem' }}>
          {status === 'connected' ? (
            <div>
              <p style={{ color: 'green', fontWeight: 'bold' }}>Successfully connected to robot!</p>
              <button 
                onClick={disconnect}
                style={{ 
                  padding: '0.5rem 1rem', 
                  backgroundColor: '#ef4444', 
                  color: 'white', 
                  borderRadius: '0.375rem',
                  border: 'none',
                  cursor: 'pointer'
                }}
              >
                Disconnect
              </button>

              {resources.length > 0 && (
                <div style={{ marginTop: '1rem' }}>
                  <h2>Available Resources:</h2>
                  <pre style={{ background: '#f5f5f5', padding: '1rem', borderRadius: '0.375rem', overflow: 'auto' }}>
                    {JSON.stringify(resources, null, 2)}
                  </pre>
                </div>
              )}
            </div>
          ) : (
            <div>
              <button 
                onClick={connect}
                style={{ 
                  padding: '0.5rem 1rem', 
                  backgroundColor: '#3b82f6', 
                  color: 'white', 
                  borderRadius: '0.375rem',
                  border: 'none',
                  cursor: 'pointer',
                  opacity: status === 'connecting' ? 0.7 : 1
                }}
                disabled={status === 'connecting'}
              >
                {status === 'connecting' ? 'Connecting...' : 'Connect'}
              </button>
              {error && (
                <p style={{ color: '#ef4444', marginTop: '0.5rem' }}>
                  {error}
                </p>
              )}
            </div>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
