import React, { useState, useEffect } from 'react';
import * as VIAM from '@viamrobotics/sdk';

function App() {
  const [status, setStatus] = useState('disconnected');
  const [error, setError] = useState(null);
  const [client, setClient] = useState(null);

  const connect = async () => {
    try {
      setStatus('connecting');
      setError(null);

      const host = 'bop2025-main.1qcq9pmop4.viam.cloud';
      console.log('Connecting to robot at:', host);

      const robotClient = await VIAM.createRobotClient({
        host,
        credentials: {
          type: 'api-key',
          payload: 'hxtk88rojiq2pzc7nasiiymnbfxr1dyc',
          authEntity: '2597529f-b98b-4e62-8c94-7f99501b0a76',
        },
        signalingAddress: 'https://app.viam.com:443',
      });

      console.log('Connected successfully!');
      setClient(robotClient);
      setStatus('connected');
    } catch (error) {
      console.error('Connection error:', error);
      setError(`Failed to connect: ${error.message || 'Unknown error'}`);
      setStatus('disconnected');
    }
  };

  const disconnect = () => {
    setClient(null);
    setStatus('disconnected');
  };

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '1rem' }}>
      <header style={{ marginBottom: '1.5rem' }}>
        <h1 style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>Viam Robot Dashboard</h1>
        <div style={{ marginTop: '0.5rem' }}>
          {status === 'connected' ? (
            <div>
              <p style={{ color: 'green', fontWeight: 'bold' }}>Successfully connected to robot!</p>
              <button 
                onClick={disconnect}
                style={{ 
                  padding: '0.5rem 1rem', 
                  backgroundColor: '#ef4444', 
                  color: 'white', 
                  borderRadius: '0.375rem',
                  border: 'none',
                  cursor: 'pointer'
                }}
              >
                Disconnect
              </button>
            </div>
          ) : (
            <div>
              <button 
                onClick={connect}
                style={{ 
                  padding: '0.5rem 1rem', 
                  backgroundColor: '#3b82f6', 
                  color: 'white', 
                  borderRadius: '0.375rem',
                  border: 'none',
                  cursor: 'pointer',
                  opacity: status === 'connecting' ? 0.7 : 1
                }}
                disabled={status === 'connecting'}
              >
                {status === 'connecting' ? 'Connecting...' : 'Connect'}
              </button>
              {error && <p style={{ color: '#ef4444', marginTop: '0.5rem' }}>{error}</p>}
            </div>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
